mapper.select:

SELECT  COUNT(*)
FROM EMP
WHERE EMP_ID = #{empId}

mapper.insert:

INSERT INTO EMP(
                        DEPT_CD
               ,        POSITION_CD
               ,        EMP_ID
               ,        EMP_PW
               ,        EMP_NAME
               ,        ADDR
               ,        ADDR_DETAIL
               ,        ZIP_CD
               ,        TEL
               ,        PHONE
               ,        ID_NUM
               ,        BIRTH_DAY
               ,        MAIL
               ,        ZALO_ID
               ,        TRANSLATION_YN
               ,        ADMIN_YN
               ,        GUBUN
               ,        STATUS
               ,        ENTER_DATE
               ,        MEMO
               ,        USE_YN
) VALUES (
             #{deptCd}
         , #{positionCd}
         , #{empId}
         , #{empPw}
         , #{empName}
         , #{addr}
         , #{addrDetail}
         , #{zipCd}
         , #{tel}
         , #{phone}
         , #{idNum}
         , #{birthDay}
         , #{mail}
         , #{zaloId}
         , #{translationYn}
         , #{adminYn}
         , #{gubun}
         , #{status}
         , #{enterDate}
         , #{memo}
         , #{useYn}
         )

mapper.update:

UPDATE EMP SET
    DEPT_CD = #{deptCd}
             ,        POSITION_CD = #{positionCd}
             ,        ADDR = #{addr}
             ,        ADDR_DETAIL = #{addrDetail}
             ,        ZIP_CD = #{zipCd}
             ,        TEL = #{tel}
             ,        PHONE = #{phone}
             ,        ID_NUM = #{idNum}
             ,        BIRTH_DAY = #{birthDay}
             ,        MAIL = #{mail}
             ,        ZALO_ID = #{zaloId}
             ,        TRANSLATION_YN = #{translationYn}
             ,        ADMIN_YN = #{adminYn}
             ,        GUBUN = #{gubun}
             ,        STATUS = #{status}
             ,        ENTER_DATE = #{enterDate}
             ,        MEMO = #{memo}
             ,        USE_YN = #{useYn}
WHERE EMP_CD = #{empCd}
