SELECT * , ROW_NUMBER() OVER (ORDER BY pj_id asc) AS ROWNUM FROM ( SELECT prj.* , GET_EMP_NAME(pla.EMP_CD, #{langCd}) AS EMP_NAME , GET_POSITION_NAME(pla.POSITION_CD, #{langCd}) AS POSITION_NAME , GET_DEPT_LEVEL_NAME(pla.DEPT_CD, #{langCd}) AS DEPT_NAME , CASE WHEN pt.pj_id IS NOT NULL then 1 ELSE 0 END AS TRANSLATION_STATUS , GET_EMP_NAME(pla.emp_cd, #{langCd}) AS leader_project_name , GET_EMP_NAME(acc.emp_cd, #{langCd}) AS leader_accounting_name , GET_POSITION_NAME(acc.POSITION_CD, #{langCd}) AS position_accounting_name , GET_DEPT_LEVEL_NAME(acc.DEPT_CD, #{langCd}) AS dept_accounting_name
, pla.dept_cd AS pla_dept_cd , dp.advance_amount , dp.remain_amount , (dp.advance_amount - dp.remain_amount) AS use_amount , CONCAT((SELECT COUNT(*) FROM day_project WHERE pj_id = prj.pj_id AND day_project_status = 'Y' AND spent_type = 'spent') , '/' , (SELECT COUNT(*) FROM day_project WHERE pj_id = prj.pj_id AND day_project_status = 'N' AND spent_type = 'spent')) as approval FROM project prj LEFT JOIN ( SELECT DISTINCT(pt.pj_id) FROM project_translation pt JOIN translation trs ON trs.translation_id = pt.translation_id AND trs.use_yn = 'Y' ) pt ON pt.pj_id = prj.pj_id LEFT JOIN project_leader_auth pla ON pla.pj_id = prj.pj_id AND pla.current_yn = 'Y' and pla.leader_type = '1' LEFT JOIN ( SELECT * FROM day_project WHERE dp_id IN ( SELECT MAX(dp_id) FROM day_project WHERE use_yn = 'Y' GROUP BY pj_id ) AND use_yn = 'Y' ) dp ON dp.pj_id = prj.pj_id LEFT JOIN project_leader_auth acc ON acc.pj_id = prj.pj_id AND acc.current_yn = 'Y' AND acc.leader_type = '2' WHERE prj.USE_YN = 'Y'
AND prj.pj_id IN ( SELECT pj_id FROM project_leader_auth WHERE emp_cd = #{empCd} AND leader_type = 2 )
AND prj.pj_id IN ( SELECT pj_id FROM project_leader_auth WHERE emp_cd = #{empCd} AND leader_type = 1 )
) result WHERE 1=1
${dateSql}
${deptSearch}
${translationStatus}
${approval}
${inputSearchSql}
ORDER BY ${str} LIMIT #{recordsPerPage} OFFSET #{startRow}

