UPDATE day_project
SET title=#{title}
WHERE dp_id = #{dpId}


UPDATE day_project
SET title=#{title},
    project_price=#{projectPrice}
WHERE dp_id = #{dpId};
UPDATE day_project
SET advance_amount=advance_amount + (${differAmount}),
    remain_amount=remain_amount + (${differAmount})
WHERE pj_id = #{pjId}
  AND dp_id >= #{dpId}
  AND use_yn = 'Y'


UPDATE day_project
SET use_yn='N'
WHERE dp_id = #{dpId};
UPDATE day_project
SET advance_amount=advance_amount - (${projectPrice}),
    remain_amount=remain_amount - (${projectPrice})
WHERE pj_id = #{pjId}
  AND dp_id >= #{dpId}
  AND use_yn = 'Y'


UPDATE day_project_item
SET use_yn='N'
WHERE dp_id = #{dpId};
UPDATE day_project
SET use_yn='N'
WHERE dp_id = #{dpId};
UPDATE day_project
SET remain_amount=remain_amount + (${projectPrice})
WHERE pj_id = #{pjId}
  AND dp_id >= #{dpId}
  AND use_yn = 'Y'

mapper.select:
SELECT *, ROW_NUMBER() OVER (ORDER BY dpi_id asc) AS ROWNUM FROM ( SELECT GET_LOCATION_STATUS(cc.c_code_name, #{langCd}) AS spent_type , ROW_NUMBER() OVER (ORDER BY dpi.dpi_id ASC) as dpi_id , 'translate' AS type , dpi.dp_id , dpi.lang_cd , dpi.emp_cd , dpi.dept_change_history_id , dpi.dept_cd , dpi.position_cd , case when dpt.contents IS NULL then "-" ELSE dpt.contents END AS product_name , dpi.price , dpi.ea , dpi.amount , dpi.check_status , dpi.img_path , dpi.reg_dt , dpi.use_yn , GET_DEPT_LEVEL_NAME(dept_cd, #{langCd}) AS dept_name , GET_POSITION_NAME(position_cd, #{langCd}) AS position_name , GET_EMP_NAME(emp_cd, #{langCd}) AS emp_name ,( SELECT COUNT(dpit.dpi_id) FROM day_project_item_translation dpit left JOIN translation t on dpit.translation_id = t.translation_id WHERE dpi_id = dpi.dpi_id AND t.use_yn = 'Y' ) AS translation_status FROM day_project_item dpi LEFT JOIN ( SELECT dpt.dpi_id, trs.contents FROM day_project_item_translation dpt JOIN translation trs ON trs.translation_id = dpt.translation_id AND trs.use_yn = 'Y' GROUP BY dpt.dpi_id ) dpt ON dpt.dpi_id = dpi.dpi_id LEFT JOIN c_code cc ON cc.c_code_value = dpi.spent_type AND cc.c_code = 'spentstatus' WHERE dpi.use_yn = 'Y' AND dp_id = #{dpId}
UNION
SELECT GET_LOCATION_STATUS(cc.c_code_name, #{langCd}) AS spent_type , ROW_NUMBER() OVER (ORDER BY dpi.dpi_id ASC) as dpi_id , ROW_NUMBER() OVER (ORDER BY dpi.dpi_id ASC) AS type , dpi.dp_id , dpi.lang_cd , dpi.emp_cd , dpi.dept_change_history_id , dpi.dept_cd , dpi.position_cd , dpi.product_name , dpi.price , dpi.ea , dpi.amount , dpi.check_status , dpi.img_path , dpi.reg_dt , dpi.use_yn , GET_DEPT_LEVEL_NAME(dept_cd, #{langCd}) AS dept_name , GET_POSITION_NAME(position_cd, #{langCd}) AS position_name , GET_EMP_NAME(emp_cd, #{langCd}) AS emp_name ,( SELECT COUNT(dpit.dpi_id) FROM day_project_item_translation dpit left JOIN translation t on dpit.translation_id = t.translation_id WHERE dpi_id = dpi.dpi_id AND t.use_yn = 'Y' ) AS translation_status FROM day_project_item dpi LEFT JOIN c_code cc ON cc.c_code_value = dpi.spent_type AND cc.c_code = 'spentstatus' WHERE dpi.use_yn = 'Y' AND dp_id = #{dpId} ) result where 1=1 ORDER BY ${str} LIMIT #{recordsPerPage} OFFSET #{startRow}

mapper.update:

UPDATE day_project
SET project_price=project_price + (${amount})
WHERE dp_id = #{dpId};
UPDATE day_project
SET remain_amount=remain_amount - (${amount})
WHERE dp_id >= #{dpId}
  AND pj_id = #{pjId}
  AND use_yn = 'Y'


UPDATE day_project_item
SET use_yn='N'
WHERE dpi_id = #{dpiId};
UPDATE day_project
SET project_price=project_price - (${amount})
WHERE dp_id = #{dpId};
UPDATE day_project
SET remain_amount=remain_amount + (${amount})
WHERE dp_id >= #{dpId}
  AND pj_id = #{pjId}
  AND use_yn = 'Y'


UPDATE day_project_item
SET check_status='Y'
WHERE dpi_id = #{dpiId};


UPDATE day_project
SET day_project_status='Y'
WHERE dp_id = #{dpId}
  AND use_yn = 'Y';
UPDATE day_project_item
SET check_status='Y'
WHERE dp_id = #{dpId}
  AND use_yn = 'Y';

mapper.insert:

INSERT INTO files
( dept_cd
, position_cd
, emp_cd
, file_type
, file_path
, file_name
, file_hash_name
, file_ext
, file_size
, reg_dt
, file_status)
VALUES ( #{deptCd}
       , #{positionCd}
       , #{empCd}
       , #{fileType}
       , #{filePath}
       , #{fileName}
       , #{fileHashName}
       , #{fileExt}
       , #{fileSize}
       , now()
       , #{fileStatus})

mapper.delete:
DELETE
FROM files
WHERE emp_cd = #{empCd}
  AND file_type = 'project'
  AND file_status = 'temp'
  AND CONCAT(file_path, file_hash_name) = #{filePathName}

DELETE
FROM day_project_file
WHERE file_id = #{fileId};
DELETE
FROM files
WHERE file_id = #{fileId}
